function timeago(c){var b=new Date();var d=new Date(c);var e=Math.floor((b-d)/1000);var a=Math.floor(e/31536000);if(a>=1){return a+" year"+(a>1?"s":"")+" ago"}a=Math.floor(e/2592000);if(a>=1){return a+" month"+(a>1?"s":"")+" ago"}a=Math.floor(e/604800);if(a>=1){return a+" week"+(a>1?"s":"")+" ago"}a=Math.floor(e/86400);if(a>=1){return a+" day"+(a>1?"s":"")+" ago"}a=Math.floor(e/3600);if(a>=1){return a+" hour"+(a>1?"s":"")+" ago"}a=Math.floor(e/60);if(a>=1){return a+" minute"+(a>1?"s":"")+" ago"}return"just now"}function updateTimeago(){$(".timeago").each(function(){if($(this).children("i").length>0){var b=$(this).children("i");var a=b.text().replace(/-/g,"/");$(this).text(timeago(a));$(this).append(b)}})}$(function(){if($(".timeago").length<=0){return}updateTimeago();setInterval(updateTimeago,60000)});